import axios from 'axios'
import { useContext } from 'react';
import { ModelContext } from '../store/model-context';

const MODEL_API_BASE_URL = "http://localhost:8080/api/v1/";

const ModelService = () => {
    const { leagueId } = useContext(ModelContext);
    
    const getPowerRankings = () => {
        return axios.get(`${MODEL_API_BASE_URL}rankings`, { headers: { 'x-league-id': leagueId } });
      };
    
    const createModel = (leagueId) => {
        return axios.post(`${MODEL_API_BASE_URL}create`, leagueId, { headers: { 'x-league-id': this.leagueId } });
      };
      return {
        getPowerRankings,
        createModel
      }
}

export default new ModelService()
